INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (264, 'Горелка газовая Emma 0.25', 'Предназначена для использования в теплогенераторах, водогрейных (жаротрубных и водотрубных) и паровых котлах', '# Газовая горелка Emma\n\n **Российская моноблочная газовая горелка Emma ** предназначена для использования в теплогенераторах, водогрейных (жаротрубных и водотрубных) и паровых котлах с разряжением в камере сгорания. Подходит для оборудования как отечественного, так и зарубежного производства с учетом аэродинамического сопротивления.  \n\n### **Ключевые особенности горелки Emma 0.25**  \n- Совместимость с отечественными и импортными блочными горелками (по функционалу, техническим параметрам и типоразмерам)  \n- Работа в условиях открытого воздуха (**от –20°C до +55°C**)  \n- Готова к эксплуатации: заводские настройки и простая конструкция сокращают время на пусконаладку и минимизируют необходимость в высококвалифицированных специалистах  \n- Плавное раздельное регулирование подачи газа и воздуха  \n- Контроль герметичности газовой арматуры  \n- Режим ручного управления мощностью  \n- Возможность подключения **датчика кислорода**  \n- Высокая защита от помех  \n\n### **Комплектация**  \n- **Газовый клапанный блок** (позиционное или плавное регулирование) производства *«Термобрест»*  \n- Датчики-реле давления воздуха и газа  \n- **Шкаф управления горелкой** (поддерживает полный алгоритм работы котельной без дополнительных устройств)  \n- Электропривод воздушной заслонки  \n- Система контроля пламени  \n- Трансформатор розжига  \n- Полный комплект для монтажа на тепловой установке  \n\nГорелка Emma обеспечивает надежную и эффективную работу котлов с минимальными затратами на обслуживание.', 117000, '/images/cutouts/emma.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Топливо","value":"Газ","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Тип","value":"Блочная газовая","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Номинальная тепловая мощность, МВт","value":" 0.25","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Напряжение электрической сети, В","value":"380","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Срок службы, лет","value":"10","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":6,"key":"Вид","value":"Горелка парового котла, Горелка водогрейного котла","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false}]', '[]', NULL, NULL, NULL, '[]');
