INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (277, ' Горелка мазутная СибСтронг любая', 'Автоматическая дутьевая блочная горелка предназначена для работы в водогрейных и паровых котлах различных конструкций и мощности с камерой сгорания под разряжением и аэродинамическим сопротивлением.', '# Мазутная горелка СибСтронг IL-2SA:\n\nАвтоматическая дутьевая блочная горелка СибСтронг предназначена для работы в водогрейных и паровых котлах различных конструкций и мощности с камерой сгорания под разряжением и аэродинамическим сопротивлением.\n\n# Функциональные характеристики горелки СибСтронг на мазуте:\n\n- Работают в одно-, двух-, трехступенчатом и модуляционном режиме.\n- Основной вид топлива - мазут, так же возможна работа на дизельном топливе.\nмПрименяется со всеми типами камеры сгорания.\n- Распыление топлива механическое, при помощи форсунки, с высоким давлением.\nПолное сжигание топлива обеспечивается с помощью регулирования соотношения количества воздуха и топлива.\n- Конструкция топливного узла специально разработана для топлива с высоким показателем вязкости и обеспечивает циркуляцию топлива во время продувки горелки.\n- Регулирование расхода воздуха при минимальном и максимальном режиме работы, обеспечивается электрическим сервоприводом. Автоматическое перекрытие доступа воздуха в топку при остановке работы горелки.\n- Горелка оборудована крепёжным фланцем и уплотнительной прокладкой, а также гибкими топливными шлангами, и форсунками.\n\n# Конструктивные особенности мазутной горелки СибСтронг:\n\n- Корпус горелки выполнен из сплава алюминия.\n- Вентилятор обеспечивает надёжную работу горелки.\n- Один трёхфазный электродвигатель приводит в действие вентилятор, второй – жидкотопливный насос.\n- Шестеренчатый насос высокого давления НШ10.\n- Форсуночный узел с механическим клапаном закрывает подачу топлива на форсунку во время продувки.\n- Топливоподогреватель, управляется электронным термостатом и дополнительным предохранительным термостатом.\n- Блок автоматического контроля и управления горелкой.\n- Контроль наличия пламени с помощью фотодатчика.\n- Щит управления включает в себя: переключатели режимов работы горелки (мазут, дизель, нагрев); индикаторы работы, блокировки и подогревателя горелки.\n- Степень защиты горелки IР40.', 1, '/images/cutouts/g_sib_strong.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Топливо","value":"Мазут","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Тип","value":"IL-2SA","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Бренд","value":"СибСтронг","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Число ступеней регулирования","value":"1","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Масса, кг","value":"42","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":6,"key":"Вид","value":"Горелка парового котла, Горелка водогрейного котла","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":7,"key":"Тепловая мощность, кВт","value":"90 - 230","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":8,"key":"Расход топлива, кг/ч","value":"8 - 21","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":9,"key":"Потребляемая электрическая мощность горелки, не более, кВт","value":"3","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":10,"key":"Мощность электродвигателя, кВт","value":"0.37","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":11,"key":"Мощность топливоподогревателя, кВт","value":"1.6","show_in_filters":false,"showKeySuggestions":false,"s