INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (279, 'Горелка дизельная Emma любая', 'Дизельная блочная горелка  применяется на теплогенераторах, водогрейных (жаротрубных и водотрубных) и паровых котлах с камерой сгорания под разряжением, и с аэродинамическим сопротивлением, как отечественного, так и импортного производства.', '# Дизельная горелка Emma 0.3:\n\nРоссийская дизельная блочная горелка Emma 0.3 применяется на теплогенераторах, водогрейных (жаротрубных и водотрубных) и паровых котлах с камерой сгорания под разряжением, и с аэродинамическим сопротивлением, как отечественного, так и импортного производства.\n\n# Особенности блочной дизельной горелки отечественного производства Emma 0.3:\n\nГорелка блочная ЕММА предназначена для сжигания дизельного топлива в условиях обеспечения рабочего диапазона кинематической вязкости. Горелка состоит из горелочного блока и блока управления. Горелочный блок состоит из жидкотопливной и воздушной части.\n\n- Жидкотопливная часть состоит из оголовка горелки, коллектора с форсунками, клапанов, датчика давления, насоса и двигателя. Топливная система от насоса через клапаны к форсункам изготовлена из медных/нержавеющих трубок, соединенных фитингами. На входе, перед насосом установлен фильтр.\n- Воздушная часть представляет собой корпус, состоящий из основания, крышки и воздухозаборника, установленной внутри корпуса крыльчатки и двигателя. На корпусе установлен датчик-реле давления. На воздухозаборнике прикреплен привод для управления шибером. Шибер регулирует подачу воздуха.', 1, '/images/cutouts/emma_diz.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Топливо","value":"Дизель","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Номинальная тепловая мощность, МВт","value":"0.15","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Максимальная тепловая мощность, МВт","value":"0.3","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Тип","value":"Блочная жидкотопливная","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Число ступеней регулирования","value":"1","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":6,"key":"Аэродинамическое сопротивление, Па (мм. вод. ст.)","value":"140","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":7,"key":"Расход топлива, кг/ч","value":"30","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":8,"key":"Давление в топке, Па","value":"220","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":9,"key":"Напряжение электрической сети, В","value":"220","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":10,"key":"Потребляемая электрическая мощность горелки, не более, кВт","value":" 0.25","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":11,"key":"Масса, кг","value":"12.5","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":12,"key":"Коэффициент избытка воздуха при номинальной тепловой мощности, не более","value":" 1.2","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":13,"key":"Срок службы, лет","value":"10","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":14,"key":"Содержание оксида углерода СО (при α=1), не более, %","value":"115","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":15,"key":"Содержание оксидов азота Nox (при α=1), не более, мг/м³","value":"160","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false}]', '[]', NULL, NULL, NULL, '[