INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (280, 'Горелка на отработанном масле любая', 'Российская горелка на отработанном масле предназначена для экономичного и эффективного сжигания жидкого топлива.', 'Основные характеристики\nмощность – 300 кВт;\nрасход топлива – 30 л/ч;\nвид топлива – отработанное масло, дизель, светлое печное топливо, темное печное топливо, керосин, нефть.\nУстройство и описание\nМноготопливная горелка на отработке 300 кВт работает в полностью автоматическом режиме. Аппараты для подачи топлива устанавливаются на любой отопительный теплоагрегат или парогенератор. Аппарат легко крепится на оборудование с помощью универсального фланца.\n\nУниверсальная горелка на отработанном масле 300 кВт производится с использованием серийных комплектующих, которые можно купить на всей территории России и СНГ. За счет этого, горелки российской сборки обходятся намного дешевле в обслуживании в отличие от импортных аналогов.', 1, '/images/cutouts/gorelka_maslo.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Бренд","value":"Отечественная горелка","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Максимальная тепловая мощность, кВт","value":"300","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Топливо","value":"Отработанное масло","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Расход топлива, л/ч","value":"30","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Диаметр дула, мм","value":"120","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":6,"key":"Подача сжатого воздуха","value":"при помощи воздушного компрессора","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":7,"key":"Давление воздуха, кг/см2","value":"до 3","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":8,"key":"Вид","value":"Горелка парового котла, Горелка водогрейного котла","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":9,"key":"Напряжение электрической сети, В","value":"220","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":10,"key":"Количество форсунок","value":"2","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":11,"key":"Число ступеней регулирования","value":"4","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":12,"key":"Тип","value":"Горелка на отработанном масле","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false}]', '[]', NULL, NULL, NULL, '[]');
INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (281, 'Горелка дизельная Baltur любая', 'Двухступенчатая дизельная горелка Baltur TBL применяется для водогрейных (жаротрубных и водотрубных) и паровых котлах с камерой сгорания под разряжением, и с аэродинамическим сопротивлением.', '# Особенности горелки Baltur TBL 45P на дизеле:\n\n- Алюминиевый корпус с шумопоглощающим декоративным кожухом.\n- Регулируемая воздушная заслонка.\n- Регулируемая подпорная шайба.\n- Вентилятор с электрическим двигателем.\n- Электронный автомат горения с самодиагностикой.\n- Трансформатор розжига.\n- Гидропривод или сервопривод воздушной заслонки.\n- Насос дизельного топлива с регулятором давления.\n- Реле давления воздуха на горение.\n- Головка горелки из нержавеющей жаропрочной стали.\n- Подвижный фланец крепления к теплогенератору.\n- Фоторезистивный датчик пламени.', 1, '/images/cutouts/gorelka_baltur_diz.jpg.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Бренд","value":"Baltur","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Тип","value":"TBL","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Топливо","value":"Дизель","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Тепловая мощность, кВт","value":" 160-450","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Число ступеней регулирования","value":"2","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":6,"key":"Вид","value":"Горелка парового котла, Горелка водогрейного котла","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":7,"key":"Количество форсунок","value":"2","show_in_filters":false,"showKeySuggestions":fal