INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (282, 'Горелка газовая ECO любые', 'Двухступенчатая механическая газовая горелка ECO применяется с водогрейными газовыми котлами с камерой сгорания под разряжением и аэродинамическим сопротивлением.', '# Особенности механической газовой горелки ECO 45 G C2b:\n\n- Оптимальное смешивание воздуха и топлива благодаря специальной головке сгорания.\n- Облегченная конструкция вентилятора высокого давления.\n- Низкий уровень шума благодаря легкому аэродинамическому корпусу, изготовленному из высококачественного алюминия.\n- Скользящий крепежный фланец для разных типов котлов.\n- Приборы автоматического контроля горелки в соответствии с европейским стандартом EN-298.\n- Простой доступ ко всему оборудованию, не снимая горелку с котла.\n- Работа с высоким КПД благодаря регулированию подачи воздуха с всасывающей линии и насадки.\n- Управление достаточной подачей газа благодаря минимальному газовому прессостату.\n- Управление воздухом для горения при помощи прессостата воздуха.\n- Простая установка и эксплуатация.\n- Вариант непосредственного зажигания и факельного зажигания (в некоторых моделях вариант факельного зажигания выполняется под заказ).\n- Возможность работы при давлении газа до 21 мбар', 1, '/images/cutouts/gorelka_eco_gaz.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Бренд","value":"ECOSTAR","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Вид","value":"Горелка парового котла, Горелка водогрейного котла","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Топливо","value":"Газ","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Тепловая мощность, кВт","value":" 385-1250","show_in_filters":false,"showKeySuggestions":f