INSERT INTO products (id, name, description, extendedDescription, price, image, category_id, specs, additional_images, delivery_set, connection_scheme, additional_requirements, required_products) VALUES (283, 'Горелка РГМГ/РМГ/ГГ любые', 'Газомазутная горелка предназначена для сжигания различных видов газового и жидкого топлива - природного газа, мазута, нефти, легкого жидкого топлива, а также дизельного, печного бытового топлива в топках котлов соответствующей тепловой мощности.', '# Горелка РГМГ-2М:\n\n- Газомазутная горелка РГМГ-2М предназначена для сжигания различных видов газового и жидкого топлива - природного газа, мазута, нефти, легкого жидкого топлива, а также дизельного, печного бытового топлива в топках котлов соответствующей тепловой мощности.\n\n- Гарантийный срок эксплуатации – 12 месяцев со дня ввода горелки в эксплуатацию, но не более 18 месяцев со дня отгрузки с предприятия-изготовителя при соблюдении потребителем условий эксплуатации, транспортирования и хранения.\n\n- Вся продукция завода сертифицирована и соответствует Техническому регламенту Таможенного союза (ТР ТС 010/2011).\n\n- На поставленную продукцию поставщик предоставляет паспорт, руководство по эксплуатации, копию сертификата соответствия, заверенную заводом изготовителем.\n', 1, '/images/cutouts/gorelka_rgmg.png', '90a753ba-6241-4468-a0b6-fb725f7854a8', '[{"id":1,"key":"Бренд","value":"Отечественная горелка","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":2,"key":"Тип","value":"РГМГ/РМГ/ГГ","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":3,"key":"Топливо","value":"Мазут, Дизель, Газ","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":4,"key":"Номинальная тепловая мощность, МВт","value":"2.2 (+0.22; -0.11)","show_in_filters":false,"showKeySuggestions":false,"showValueSuggestions":false},{"id":5,"key":"Коэффициент рабочего регулирования, не менее","value":"4","show_in_filter